INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('1', 'Apple 2020 MacBook Air Laptop M1 Chip, Backlit Keyboard', '1', '23200000', '18000000', '1', '2023-04-18', '10','"MacBook Air 13.3"" with Retina Display - 30W USB Type-C Power Adapter - USB Type-C Charge Cable (2m) - Apple 1 Year Limited Warranty with 90 Day Technical Support Apple M1 chip with 8-core CPU and 7-core GPU 16-core Neural Engine 8GB unified memory 512GB SSD storage');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('2', 'Apple 2021 MacBook Pro - Space Gray - Z14X000HQ', '1', '19000000', '18000000', '1', '2023-04-19', '10','Apple M1 Max chip for a massive leap in CPU, GPU, and machine learning performance 10-core CPU delivers up to 2x faster performance to fly through pro workflows quicker than ever 32-core GPU with up to 4x faster performance for graphics-intensive apps and games 16-core Neural Engine for up to 5x faster machine learning performance Longer battery life, up to 21 hours 64GB of unified memory so everything you do is fast and fluid 2TB of superfast SSD storage launches apps and opens files in an instant ');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('3', 'Apple 2022 MacBook Air Laptop with M2 chip 13.6-inch Liquid Retina Display', '1', '24000000', '17500000', '1', '2023-04-20', '10','STRIKINGLY THIN DESIGN — The redesigned MacBook Air is more portable than ever and weighs just 2.7 pounds. It’s the incredibly capable laptop that lets you work, play or create just about anything — anywhere. SUPERCHARGED BY M2 — Get more done faster with a next-generation 8-core CPU, up to 10-core GPU and up to 24GB of unified memory. UP TO 18 HOURS OF BATTERY LIFE — Go all day and into the night, thanks to the power-efficient performance of the Apple M2 chip. BIG, BEAUTIFUL DISPLAY — The 13.6-inch Liquid Retina display features over 500 nits of brightness, P3 wide color and support for 1 billion colors for vibrant images and incredible detail. ADVANCED CAMERA AND AUDIO — Look sharp and sound great with a 1080p FaceTime HD camera, three-mic array and four-speaker sound system with Spatial Audio. VERSATILE CONNECTIVITY — MacBook Air features a MagSafe charging port, two Thunderbolt ports and a headphone jack. EASY TO USE — Your Mac feels familiar from the moment you turn it on, and works seamlessly with all your Apple devices.');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('4', 'Apple MacBook Pro 16 with Liquid Retina XDR Display', '1', '27000000', '16500000', '1', '2023-04-21', '10','Apple MacBook Pro 16"" with Liquid Retina XDR Display - 140W USB-C Power Adapter - USB-C to MagSafe 3 Cable (2m) - Apple 1 Year Limited Warranty Apple M1 Max chip for a massive leap in CPU, GPU, and machine learning performance 10-core CPU delivers up to 2x faster performance to fly through pro workflows quicker than ever 24-core GPU with up to 4x faster performance for graphics-intensive apps and games 16-core Neural Engine for up to 5x faster machine learning performance ');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('5', 'ASUS ROG Strix G15 (2022) Gaming Laptop G513RC-IS74', '1', '22000000', '17500000', '1', '2023-04-22', '10','SUPERCHARGED RTX GRAPHICS - Gameplay graphics are silky smooth with the NVIDIA GeForce RTX 3050 4GB GDDR6 at 95W with ROG Boost, with cutting-edge AI features like NVIDIA DLSS and Ray-Tracing MUX SWITCH BOOST - A MUX Switch lets the GPU communicate directly with the display, increasing performance and decreasing latency ROG INTELLIGENT COOLING - a robust thermal system with Thermal Grizzly Liquid Metal Thermal Compound and self cleaning dual fans with anti-dust technology extend system longevity READY FOR ANYTHING - Use your gaming laptop to stream and multi-task with ease thanks to an AMD Ryzen 7 6800H CPU with 20MB Cache, up to 4.7 GHz, 8 cores/16 threads and 16GB of blisteringly fast 4800MHz DDR5 RAM on Windows 11 Home SWIFT VISUALS – Stay one step ahead of the competition thanks to its 300Hz/3ms 15.6” Full HD (1920 x 1080) IPS Type Display, which covers 100% sRGB color space AMPLE STORAGE FOR ALL YOUR GAMES - Store all your game library, and load them fast on your 1TB PCIe NVMe M.2 SSD to take advantage of the full RTX gaming experience AURA SYNC - 4-zone RGB Keyboard, RGB light bar, RGB Logo ');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('6', 'ASUS ROG Strix G16 (2023) Gaming Laptop G614JI-AS94', '1', '27500000', '22000000', '1', '2023-04-23', '10','POWER UP YOUR PLAY - Draw more frames and win more games with Windows 11, a 13th Gen Intel Core i9-13980HX processor, and an NVIDIA GeForce RTX 4070 Laptop GPU at 140W Max TGP. BLAZING FAST MEMORY AND STORAGE – Multitask swiftly with 16GB of DDR5-4800MHz memory and speed up loading times with 1TB of PCIe 4x4. ROG INTELLIGENT COOLING – To put this amount of power in a gaming laptop, you need an even better cooling solution. The Strix features Thermal Grizzly’s Conductonaut Extreme liquid metal on the CPU, and a third intake fan among other premium features, to allow for better sustained performance over long gaming sessions. SWIFT VISUALS – The Strix G16 has a fast FHD 165Hz panel to make sure you never miss a moment. It also covers 100% of the sRGB color space and feature Dolby Vision, Adaptive-Sync support, and an 90% screen-to-body ratio for a stellar gaming and viewing experience. MUX SWITCH WITH ADVANCED OPTIMUS - A MUX Switch increases laptop gaming performance by 5-10% by routing frames directly from the dGPU to the display bypassing the iGPU. With Advanced Optimus the switch between iGPU and dGPU becomes automatic based on the task, optimizing battery life.');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('7', 'ASUS TUF Dash 15 (2022) Gaming Laptop Off Black, FX517ZM-AS73', '1', '18900000', '18000000', '1', '2023-04-24', '10','SUPERCHARGED RTX GRAPHICS - Gameplay graphics are silky smooth with the NVIDIA GeForce RTX 3060 6GB GDDR6 at 1050W with Dynamic Boost, with cutting-edge AI features like NVIDIA DLSS and Ray-Tracing MUX SWITCH BOOST - A MUX Switch lets the GPU communicate directly with the display, increasing performance and decreasing latency READY FOR ANYTHING - Use your gaming laptop to stream and multi-task with ease thanks to an  Intel Core i7-12650H Processor with 24MB Cache, up to 4.7 GHz, 10 cores (6 P-cores and 4 E-cores) and 16GB of blisteringly fast 4800MHz DDR5 RAM on Windows 11 SWIFT VISUALS – Stay one step ahead of the competition thanks to its 144Hz 15.6” Full HD (1920 x 1080) IPS Type Display AMPLE STORAGE FOR ALL YOUR GAMES - Store all your game library, and load them fast on your 512GB PCIe NVMe M.2 SSD to take advantage of the full RTX gaming experience STAY COOL - Dual Self cleaning Arc Flow fans with anti-dust technology extend system longevity MILITARY GRADE TOUGHNESS - Durable MIL-STD-810H military standard lives in the TUF line as the devices are tested against drops, vibration, humidity and extreme temperatures to ensure reliability');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('8', 'ASUS VivoBook 15X OLED Laptop M1503QA-ES74', '1', '19000000', '18000000', '1', '2023-04-25', '10','15.6” FHD (1920 x 1080) OLED 16:9 aspect ratio display with ultra-slim NanoEdge bezels 0.2ms response time, 600nits peak brightness, 100% DCI-P3 color gamut, 1,000,000:1 Contrast ratio, VESA CERTIFIED Display HDR True Black 600 Latest AMD Ryzen 7 5800H Mobile Processor (8-core/16-thread, 20MB cache, up to 4.4 GHz max boost) and AMD Radeon Graphics Fast storage and memory featuring 512GB M.2 NVMe PCIe 3.0 SSD and 8GB DDR4 on board + 8GB DDR4 SO-DIMM RAM, Windows 11 Home Extensive connectivity with 2x USB 3.2 Gen 1 Type-C, USB 2.0 Type A, 3.5mm Combo Audio Jack, Wi-Fi 6(802.11ax)+Bluetooth 5.0 (Dual band) 2*2 (*USB transfer speed may vary. Learn more at ASUS.com)');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('9', 'ASUS Vivobook Laptop L210 11.6 Ultra Thin Laptop L210MA-DS04', '1', '20000000', '18000000', '1', '2023-04-26', '10','Powerful Productivity: Efficient Intel Celeron N4020 Processor 1.1 GHz (4M Cache, up to 2.8 GHz) and Intel UHD graphics delivers unmatched speed and intelligence, enabling impressive creating and productivity Visibly Stunning: Experience sharp details and crisp colors on the 11.6” HD (1366 x 768) Slim Display with 73% screen-to-body ratio, 16:9 aspect ratio Internal Specifications: 128GB eMMC Flash Storage and 4GB DDR4 RAM to store your files Secure Operating System: Windows 11 Home in S mode comes with advanced security features built right in, like protections against phishing and malicious software so you dont have to think twice when navigating to a new webpage or downloading an app Ports For All Your Accessories: USB 3.2 Gen 1 Type-C, USB 3.2 Gen 1 Type-A, HDMI (*USB Transfer speed may vary. Learn more at ASUS website) Slim and Portable: 0.7” thin and weighs only 2.31 lbs (battery included) Whats In the Box: ASUS Vivobook laptop, AC adapter, Power Cord, User manual, Warranty card');
INSERT INTO products (id, name, shop_id, begin_price, discount_price, category_id, date, quantity, description) VALUES ('10', 'Lenovo 14inch HD Laptop, Intel Pentium Silver ', '1', '19000000', '18000000', '1', '2023-04-27', '10','14inch HD LED Backlit Display with Anti-Glare Technology (1366 x 768), Integrated Intel UHD 605 Graphics Intel Pentium Silver Quad-Core N5030 Processor Up to 3.10 GHz, 128GB NVMe SSD, 4GB DDR4 Ram 1x HDMI, 2x USB Ports, 1x Media Card Reader, 1x Headphone/microphone Combo Jack Integrated Webcam and Dual Array Microphones, Dolby Audio, 802. 11AC Wi-Fi and Bluetooth 4.1 Combo Windows 10 OS in S Mode, A JJTK 16GB USB Drive Included');
